(function(){
    // Local wrapper that dynamically loads the CDN copy of chosen if the real library isn't present.
    // This keeps repository small while providing a local entrypoint that can be replaced with the
    // actual minified library file if you prefer to vendor it fully.
    function loadScript(src, cb) {
        var s = document.createElement('script'); s.src = src; s.async = false; s.onload = cb; s.onerror = cb; document.head.appendChild(s);
    }
    if (window.jQuery && typeof jQuery.fn.chosen === 'function') {
        // already present
        return;
    }
    // try load from CDN
    loadScript('https://cdnjs.cloudflare.com/ajax/libs/chosen/1.8.7/chosen.jquery.min.js', function(){
        if (!window.jQuery || typeof jQuery.fn.chosen !== 'function') {
            console.warn('Chosen library failed to load from CDN; consider vendoring chosen.jquery.min.js locally in wwwroot/lib/chosen/');
        }
    });
})();
